import React from "react";
import "./InputLabel.css";

interface InputLabelProps {
  htmlFor: string;
  label: string;
  size?: "small" | "medium" | "large";
  isRequired?: boolean;
  hint?: string;
}

const InputLabel: React.FC<InputLabelProps> = ({
  htmlFor,
  label,
  size = "large",
  isRequired = true,
  hint = "required",
}) => {
  return (
    <label className={`input-label ${size}`} htmlFor={htmlFor}>
      {label} {isRequired && <span className="required">*</span>}
      {isRequired && hint && <span className="hint">{hint}</span>}
    </label>
  );
};

export default InputLabel;
