import React from "react";
import "./InputText.css";

interface InputTextProps {
  id: string;
  placeholder?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  size?: "small" | "medium" | "large" | "extra-large";
  quiet?: boolean;
  disabled?: boolean;
  hasError?: boolean;
  helpText?: string;
  description?: string;
}

const InputText: React.FC<InputTextProps> = ({
  id,
  placeholder,
  value,
  onChange,
  size = "extra-large",
  quiet = false,
  disabled = false,
  hasError = true,
  helpText,
  description,
}) => {
  return (
    <div className="input-text-container">
      <input
        id={id}
        type="text"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className={`input-text ${size} ${quiet ? "quiet" : ""} ${hasError ? "error" : ""}`}
        disabled={disabled}
      />
      {hasError && <span className="error-message">{helpText}</span>}
      {!hasError && (
        <span className="description-message">
          {description}
          Passwords must be at least 8 characters
        </span>
      )}
    </div>
  );
};

export default InputText;
