import React from "react";
import "./InputAnnotation.css";
import "../../styles/GlobalStyles.css";

interface InputAnnotationProps {
  helpText?: string;
  description?: string;
  hasError?: boolean;
}

const InputAnnotation: React.FC<InputAnnotationProps> = ({
  helpText,
  description,
  hasError,
}) => {
  return (
    <div className="input-annotation">
      {hasError && helpText && <span className="helpText">{helpText}</span>}
      {!hasError && description && (
        <span className="input-annotation.description">{description}</span>
      )}
    </div>
  );
};

export default InputAnnotation;
