import React from "react";
import "./InputText.css";

interface InputTextProps {
  id: string;
  placeholder?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  size?: "small" | "medium" | "large" | "extra-large";
  quiet?: boolean;
  disabled?: boolean;
  hasError?: boolean;
  description?: string;
}

const InputText: React.FC<InputTextProps> = ({
  id,
  placeholder,
  value,
  onChange,
  size = "small",
  quiet = false,
  disabled = false,
  hasError = true,
  description,
}) => {
  return (
    <div className={`input-text-container ${hasError ? "error" : ""}`}>
      <input
        id={id}
        type="text"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className={`input-text ${size} ${quiet ? "quiet" : ""}`}
        disabled={disabled}
      />
      {hasError && (
        <span className="error-message">Invalid input. Try again</span>
      )}
      {!hasError && description && (
        <span className="description-message">{description}</span>
      )}
    </div>
  );
};

export default InputText;
