import React from "react";
import "./InputText.css";

interface InputTextProps {
  id: string;
  placeholder?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  size?: "small" | "medium" | "large" | "extra-large";
  quiet?: boolean;
  disabled?: boolean;
  hasError?: boolean;
}

const InputText: React.FC<InputTextProps> = ({
  id,
  placeholder,
  value,
  onChange,
  size = "small",
  quiet = false,
  disabled = false,
  hasError = false,
}) => {
  return (
    <div className={`input-text-container ${hasError ? "error" : ""}`}>
      <input
        id={id}
        type="text"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className={`input-text ${size} ${quiet ? "quiet" : ""}`}
        disabled={disabled}
      />
      {hasError && (
        <span className="error-message">
          Это поле обязательно для заполнения
        </span>
      )}
    </div>
  );
};

export default InputText;
