import React from "react";
import InputLabel from "../../atoms/InputLabel/InputLabel";
import InputText from "../../atoms/InputText/InputText";
import InputAnnotation from "../../atoms/InputAnnotation/InputAnnotation";
import "./InputGroup.css";

interface InputGroupProps {
  id: string;
  label: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  isRequired?: boolean;
  hasError?: boolean;
  placeholder?: string;
  size?: "small" | "medium" | "large" | "extra-large";
  quiet?: boolean;
  disabled?: boolean;
}

const InputGroup: React.FC<InputGroupProps> = ({
  id,
  label,
  value,
  onChange,
  isRequired = false,
  hasError = false,
  placeholder,
  size,
  quiet,
  disabled,
}) => {
  return (
    <div className={`input-group ${hasError ? "error" : ""}`}>
      <InputLabel htmlFor={id} label={label} isRequired={isRequired} />
      <InputText
        id={id}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        size={size}
        quiet={quiet}
        disabled={disabled}
      />
      {errorText ? (
        <InputAnnotation text={errorText} isError />
      ) : (
        helpText && <InputAnnotation text={helpText} />
      )}
    </div>
  );
};

export default InputGroup;
