import React from "react";

interface InputAnnotationProps {
  helpText?: string;
  description?: string;
  hasError?: boolean;
}

const InputAnnotation: React.FC<InputAnnotationProps> = ({
  helpText,
  description,
  hasError,
}) => {
  return (
    <div className="input-annotation">
      {hasError && helpText && <span className="info">{helpText}</span>}
      {!hasError && description && <span className="error">{description}</span>}
    </div>
  );
};

export default InputAnnotation;
